import { useState } from "react";

const useHttp = (requestConfig, applyData) => {
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const sendRequest = async () => {
    setIsLoading(true);
    setError(null);
    try {
      // NOTE: Since we will be calling that hook, we then have to make sure that we pass in an object which has a URL property, which holds the URL.
      const response = await fetch(requestConfig.url, {
        method: requestConfig.method,
        headers: requestConfig.headers,
        body: JSON.stringify(requestConfig.body),
      });

      if (!response.ok) {
        throw new Error("Request failed!");
      }

      const data = await response.json();
      applyData(data);
    } catch (err) {
      setError(err.message || "Something went wrong!");
    }
    setIsLoading(false);
  };

  return {
    // NOTE: This is a modern JS shortcut, it is the same as {isLoading: isLoading, error: error...}
    isLoading,
    error,
    sendRequest,
  };
};

export default useHttp;
